from __future__ import (absolute_import, division, print_function, unicode_literals)import backtraderfrom datetime import datetimeclass RSI(backtrader.Strategy):   params = (('short', 30),  # 買入點             ('long', 70),)  # 賣出點   def __init__(self):      self.rsi = backtrader.indicators.RSI_SMA(self.data.close, period=14)   def next(self):      if not self.position:         if self.rsi < self.params.short:            self.buy()      else:         if self.rsi > self.params.long:            self.sell()def main():   startcash = 10000   data = backtrader.feeds.YahooFinanceCSVData(dataname = './ETH-USD.csv')   cerebro = backtrader.Cerebro()   cerebro.adddata(data)   cerebro.addstrategy(RSI)   cerebro.broker.setcash(startcash)   cerebro.run()   portvalue = cerebro.broker.getvalue()   pnl = portvalue - startcash   print('Final Portfolio Value: ${}'.format(portvalue))   print('P/L: ${}'.format(pnl))   cerebro.plot(style = "candlestick")if __name__ == '__main__':   main()